import React from 'react';
import logo from './logo.svg';
import './App.css';
import Button from 'react-bootstrap/Button'
import NavbarPage from './NavbarPage';
import {BrowserRouter, Route} from 'react-router-dom'
import { MDBTable, MDBTableBody, MDBTableHead } from 'mdbreact';


/*function App() {
  return (
   <div className="App">
      <header className="App-header">
        <h1 id='title'>React Dynamic Table</h1>
            <table id='students'>
               <tbody>
                  <tr>{this.renderTableHeader()}</tr>
                  {this.renderTableData()}
               </tbody>
            </table>
      </header>
    </div>
  );
}
*/

class Home extends React.Component {
  state = {
    todos: []
  }



  componentDidMount() {
    fetch('http://localhost:5000/api/Inventory')
    .then(res => res.json())
    .then((data) => {
      this.setState({ todos: data })
      console.log(this.state.todos)
    })
    .catch(console.log)
  }
  /* constructor(props) {
      super(props)
      this.state = {
         students: [
            { id: 1, name: 'Wasif', age: 21, email: 'wasif@email.com' },
            { id: 2, name: 'Ali', age: 19, email: 'ali@email.com' },
            { id: 3, name: 'Saad', age: 16, email: 'saad@email.com' },
            { id: 4, name: 'Asad', age: 25, email: 'asad@email.com' }
         ]
      }
   }*/

   renderTableHeader() {

    /*let header = Object.keys(this.state.todos[1])
      return header.map((key, index) => {
         return <th key={index}>{key.toUpperCase()}</th>
      })*/
     return (
      <tr>
       <th>HOSTNAME</th>
        <th>IP</th>
        <th>ENVIRONMENT</th>
        <th>MANUFACTURER</th>
        <th>ACTIVE</th>
        <th>TAG</th>
        <th>HOSTCATEGORY</th>
        <th>NAME</th>
        <th>VERSION</th>
        <th>PRODUCTTEAM</th>
        <th>EMAIL</th>       
        <th>FREQUENCY</th>
        <th>DAYOFMONTH </th>
        <th>DAYOFWEEK</th>
        <th>HOURSTART</th>
        <th>MINSTART</th>
        <th>DURATION</th>
        <th>REBOOT</th>
        <th>TAKESNAPSHOT</th>
        <th>ENABLE</th>
        <th>UPDATETIME</th>
        <th>CREATETIME</th>
        <th>STATUS</th>
        <th>ORG</th>
        <th>LASTPATCHTIME</th>
        <th></th>
      </tr>
      )
   }

   renderTableData() {
      return this.state.todos.map((todo, index) => {
         const { userId, id, title, completed } = todo //destructuring
         return (
            <tr key={todo.hostName}>
               <td>{todo.hostName}</td>
               <td>{todo.ip}</td>
               <td>{todo.environment}</td>
               <td>{todo.manufacturer}</td>
               <td>{todo.active}</td>
               <td>{todo.tag}</td>               
               <td>{todo.hostCategory}</td>
               <td>{todo.os.name}</td>
               <td>{todo.os.version}</td>              
               <td>{todo.owner.productTeam}</td>
               <td>{todo.owner.email}</td>      
               <td>{todo.outageWindow.frequency}</td>
               <td>{todo.outageWindow.dayOfMonth}</td>
               <td>{todo.outageWindow.dayOfWeek}</td>
               <td>{todo.outageWindow.hourStart}</td>
               <td>{todo.outageWindow.minStart}</td>
               <td>{todo.outageWindow.duration}</td>
               <td>{todo.vcenter.reboot}</td>
               <td>{todo.vcenter.takeSnapshot}</td>
               <td>{todo.validation.enable}</td>
               <td>{todo.updateTime}</td>
               <td>{todo.createTime}</td>
               <td>{todo.status}</td>
               <td>{todo.org}</td>
               <td>{todo.lastPatchTime}</td>
               <td></td>
            </tr>
         )
      })
   }

   render() {
    const options = {
        filterType: "dropdown",
        responsive: "scroll",
        rowsPerPage: 10,
        selectableRows: "none",
        viewColumns: true,
        sort: true
      };
      return (
         <div class="container-fluid">
            <h1 id='title'>PATCH IT FRAMEWORK</h1>
            <table id='students'>
               <tbody class='tbody'>
                  {this.renderTableHeader()}
                  {this.renderTableData()}
               </tbody>
            </table>
         </div>
      )
   }
}
export default Home;
