import React, { Component } from "react";
import ReactDOM from 'react-dom';
import './index.css';
import './modal.css';
import './App.css'
import { createSingleInventoryPost } from './createSingleInventoryPost';
import axios from 'axios'



class SingleInventory extends Component {


   constructor(props) {
    super(props)

    this.state = {
      hostName: '',
      ip: '',
      environment: 'DEV', 
      manufacturer: 'vmware', 
      active: 'N', 
      tag: '', 
      hostCategory: 'VM', 
      name: 'RHEL', 
      version: '', 
      productTeam: '', 
      email: '', 
      frequency: 'Monthly', 
      dayOfMonth: '', 
      dayOfWeek: '', 
      hourStart: '',  
      minStart: '', 
      duration: '',  
      reboot: 'N', 
      takeSnapshot: 'Y', 
      enable: '', 
      status: 'ready',  
      org: 'CORP'
    }
  }

  changeHandler = e => {
    this.setState({ [e.target.name]: e.target.value })

  }
   onChangeEnvironment(ev1) {
    this.setState({environment: ev1.target.value});
    alert(ev1.target.value)
  }
    onChangeManufacturer(ev2) {
    this.setState({manufacturer: ev2.target.value});
    alert(ev2.target.value)
  }
    onChangeActive(ev3) {
    this.setState({active: ev3.target.value});
    alert(ev3.target.value)
  }
    onChangeHostCatergory(ev4) {
    this.setState({hostCategory: ev4.target.value});
    alert(ev4.target.value)
  }
    onChangeName(ev5) {
    this.setState({name: ev5.target.value});
    alert(ev5.target.value)
  }
    onChangeFrequency(ev6) {
    this.setState({frequency: ev6.target.value});
    alert(ev6.target.value)
  }
   onChangeReboot(ev) {
    this.setState({reboot: ev.target.value});
    alert(ev.target.value)
  }
    onChangeTakeSnapShot(ev7) {
    this.setState({takeSnapshot: ev7.target.value});
    alert(ev7.target.value)
  }
    onChangeStatus(ev8) {
    this.setState({status: ev8.target.value});
    alert(ev8.target.value)
  }
    onChangeOrg(ev9) {
    this.setState({org: ev9.target.value});
    alert(ev9.target.value)
  }




    submitHandler = e => {
    e.preventDefault()
    console.log(this.state)
    axios
      .post('http://localhost:5000/api/post', this.state,{
  'Content-Type': 'application/json',
  'Access-Control-Allow-Origin': '*'
})
